import react from 'react';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SendPosts() {


const [posts, setPosts] = useState({
    title: '',
    description: '',
});  



    useEffect(() => {
        axios.post('http://localhost:3000/posts/', posts )
        .then(res =>{
            console.log(res)
        })
        .catch(err => {
            console.log(err)
        })

    }, []);

    const handleSubmit = (e) => {
        e.prevebtDefault();
        alert('submitet title' , {posts})
    }



  return (
   <div>
     <form onSubmit={handleSubmit} >
         <div>
             <input type="text " name="title"  value={posts.title}  onChange={(e => setPosts.target.value)}  />
         </div>
         <div>
             <input type="text " name="description" value={posts.description} />
         </div>
         <button type="submit"> Sign Up  </button>
     </form>
   </div>
  );
}

export default SendPosts;
